vector<int> states;         // States of all gates (0 to N+M-1)
vector<int> thresholds;     // Threshold values for threshold gates
vector<int> input_counts;   // Number of inputs contributing to each gate's state

void init(int n, int m, vector<int> p, vector<int> a) {
    N = n;
    M = m;
    P = p;
    A = a;

    inputs.assign(N, vector<int>());
    states.assign(N + M, 0);
    thresholds.assign(N, 0);
    input_counts.assign(N, 0);

    // Initialize the inputs for each threshold gate
    for (int i = 1; i < N + M; ++i) {
        inputs[P[i]].push_back(i);
    }

    // Initialize the states of source gates
    for (int j = 0; j < M; ++j) {
        states[N + j] = A[j];
    }

    // Calculate initial states for threshold gates
    for (int i = N + M - 1; i >= 0; --i) {
        if (i < N) { // Threshold gates
            int count_ones = 0;
            for (int input_gate : inputs[i]) {
                if (states[input_gate] == 1) {
                    ++count_ones;
                }
            }
            input_counts[i] = count_ones;
            thresholds[i] = inputs[i].size(); // Initially set to the max possible threshold
            states[i] = (count_ones >= thresholds[i]) ? 1 : 0;
        }
    }
}

int count_ways(int L, int R) {
    // Toggle the states of source gates in range [L, R]
    for (int i = L; i <= R; ++i) {
        states[i] ^= 1; // Toggle state
    }

    // Propagate state changes to threshold gates
    queue<int> q;
    vector<bool> visited(N, false);

    for (int i = L; i <= R; ++i) {
        int parent = P[i];
        if (parent >= 0 && !visited[parent]) {
            q.push(parent);
            visited[parent] = true;
        }
    }

    while (!q.empty()) {
        int gate = q.front();
        q.pop();

        int count_ones = 0;
        for (int input_gate : inputs[gate]) {
            if (states[input_gate] == 1) {
                ++count_ones;
            }
        }

        input_counts[gate] = count_ones;
        states[gate] = (count_ones >= thresholds[gate]) ? 1 : 0;

        if (gate > 0 && !visited[P[gate]]) {
            q.push(P[gate]);
            visited[P[gate]] = true;
        }
    }

    // Calculate the number of valid parameter assignments for gate 0
    int result = 1;
    for (int i = 0; i < N; ++i) {
        if (states[i] == 1) {
            result = (1LL * result * input_counts[i]) % MOD;
        }
    }

    return result;
}

