vector<int> states; // Current states of all gates
vector<vector<int>> inputs; // Inputs for each threshold gate
vector<int> thresholds; // Thresholds for each threshold gate
vector<int> inputCount; // Number of inputs for each threshold gate

// Helper function to compute power modulo MOD
int modPow(int base, int exp) {
    int result = 1;
    while (exp > 0) {
        if (exp % 2 == 1) result = (1LL * result * base) % MOD;
        base = (1LL * base * base) % MOD;
        exp /= 2;
    }
    return result;
}

// Initialize the system
void init(int n, int m, vector<int> p, vector<int> a) {
    N = n;
    M = m;
    P = p;
    A = a;

    // Initialize variables
    states.assign(N + M, 0);
    inputs.assign(N, vector<int>());
    thresholds.assign(N, 0);
    inputCount.assign(N, 0);

    // Set up initial states of source gates
    for (int i = 0; i < M; ++i) {
        states[N + i] = A[i];
    }

    // Build input lists for each threshold gate
    for (int i = 1; i < N + M; ++i) {
        inputs[P[i]].push_back(i);
    }

    // Set input counts for threshold gates
    for (int i = 0; i < N; ++i) {
        inputCount[i] = inputs[i].size();
    }
}

// Update the states of the gates after toggling source gates
void updateStates(int L, int R) {
    // Toggle the states of source gates in the range [L, R]
    for (int i = L; i <= R; ++i) {
        states[i] = 1 - states[i];
    }

    // Reset states of threshold gates
    for (int i = 0; i < N; ++i) {
        thresholds[i] = 0;
    }

    // Perform a topological update of the gate states
    for (int i = N + M - 1; i >= 0; --i) {
        if (i >= N) continue; // Skip source gates

        // Count the number of active inputs
        int activeCount = 0;
        for (int input : inputs[i]) {
            if (states[input] == 1) {
                ++activeCount;
            }
        }

        // Update the state of the threshold gate
        states[i] = (activeCount >= thresholds[i]) ? 1 : 0;
    }
}

// Count the number of ways to assign parameters such that gate 0 has state 1
int countWays(int L, int R) {
    // Update the gate states
    updateStates(L, R);

    // Compute the number of ways threshold gates can be assigned
    if (states[0] == 0) return 0; // If gate 0 is not active, no valid assignment

    int totalWays = 1;
    for (int i = 0; i < N; ++i) {
        // The number of valid thresholds for this gate
        int validThresholds = (states[i] == 1) ? inputCount[i] : 0;

        // Multiply the number of ways
        totalWays = (1LL * totalWays * validThresholds) % MOD;
    }

    return totalWays;
}

