const int MOD = 1000002022;

int N, M;
vector<int> P, A;
vector<vector<int>> inputs; // To store the inputs of each threshold gate
vector<int> state; // To store the current state of all gates (threshold + source)
vector<int> threshold_gate_inputs; // Number of inputs for each threshold gate

// Precomputed powers of 2 modulo MOD for fast computation of parameter assignments
vector<int> power_of_2;

// Precomputes the power of 2 modulo MOD up to the maximum number of inputs
void precompute_powers_of_2(int max_inputs) {
    power_of_2.resize(max_inputs + 1);
    power_of_2[0] = 1;
    for (int i = 1; i <= max_inputs; ++i) {
        power_of_2[i] = (2 * power_of_2[i - 1]) % MOD;
    }
}

// Initializes the input data and prepares for efficient computation
void init(int n, int m, vector<int> p, vector<int> a) {
    N = n;
    M = m;
    P = p;
    A = a;

    // Initialize state vector
    state.resize(N + M, 0);
    for (int i = 0; i < M; ++i) {
        state[N + i] = A[i];
    }

    // Initialize inputs list for each threshold gate
    inputs.resize(N);
    threshold_gate_inputs.resize(N, 0);
    for (int i = 1; i < N + M; ++i) {
        inputs[P[i]].push_back(i);
        if (P[i] < N) {
            ++threshold_gate_inputs[P[i]];
        }
    }

    // Precompute powers of 2 modulo MOD
    precompute_powers_of_2(*max_element(threshold_gate_inputs.begin(), threshold_gate_inputs.end()));
}

// Updates the states of the source gates in the specified range
void toggle_source_gates(int L, int R) {
    for (int i = L; i <= R; ++i) {
        state[i] = 1 - state[i]; // Toggle the state
    }
}

// Computes the state of a threshold gate based on its inputs
int compute_gate_state(int gate_id) {
    int active_inputs = 0;
    for (int input_gate : inputs[gate_id]) {
        active_inputs += state[input_gate];
    }
    return active_inputs;
}

// Counts the number of ways to assign parameters such that gate 0 has state 1
int count_state_one_ways() {
    // BFS or topological sorting approach to compute gate states
    queue<int> q;
    vector<int> computed_state(N, -1); // -1 means not computed yet

    // Start with source gates and propagate to threshold gates
    q.push(0);
    while (!q.empty()) {
        int current_gate = q.front();
        q.pop();

        // Compute the state of the current gate
        int active_inputs = compute_gate_state(current_gate);
        computed_state[current_gate] = active_inputs;

        // Push dependent gates into the queue
        for (int input_gate : inputs[current_gate]) {
            if (input_gate < N) { // Threshold gates only
                q.push(input_gate);
            }
        }
    }

    // Compute the number of valid assignments for gate 0
    int gate_0_inputs = computed_state[0];
    if (gate_0_inputs == 0) {
        return 0; // No way to make gate 0 state 1
    }
    return (power_of_2[gate_0_inputs - 1]) % MOD;
}

// Handles the toggle operation and computes the result
int count_ways(int L, int R) {
    toggle_source_gates(L, R);
    return count_state_one_ways();
}

