void create_circuit(int M, vector<int> A) {
    int N = A.size();
    vector<int> C(M + 1); // Connections for origin and triggers
    vector<int> X;       // Connections for switches (exit X)
    vector<int> Y;       // Connections for switches (exit Y)

    // Map triggers to switches to avoid excess usage of switches
    vector<int> last_used_trigger(M + 1, -1); // Tracks the last switch used for each trigger
    int switch_counter = 0; // Counter for switches

    // Connect origin to the first trigger in A
    C[0] = A[0];

    for (int i = 0; i < N; i++) {
        int current_trigger = A[i];
        int next_trigger = (i == N - 1) ? 0 : A[i + 1]; // Connect to origin if last trigger

        // Check if we need a new switch for this transition
        if (last_used_trigger[current_trigger] == -1) {
            // Create a new switch
            switch_counter++;
            last_used_trigger[current_trigger] = -switch_counter;
            X.push_back(next_trigger); // Connect exit X to the next trigger
            Y.push_back(current_trigger); // Connect exit Y to the current trigger
        } else {
            // Reuse the existing switch
            int switch_id = last_used_trigger[current_trigger];
            X[-switch_id - 1] = next_trigger; // Update exit X
        }

        // Connect the current trigger to its corresponding switch
        C[current_trigger] = last_used_trigger[current_trigger];
    }

    // Provide the answer to the grader
    answer(C, X, Y);
}

