    int last_switch = 0;  // The last switch created

    // Map triggers in sequence A
    vector<int> trigger_to_next(M + 1, 0);
    for (int i = 0; i < N; ++i) {
        if (i < N - 1) {
            trigger_to_next[A[i]] = A[i + 1];
        } else {
            trigger_to_next[A[i]] = 0;  // Last trigger loops to origin
        }
    }

    // Create the circuit
    for (int i = 0; i <= M; ++i) {
        if (trigger_to_next[i] == 0) {
            C[i] = 0;  // Connect to origin
        } else {
            // Create a new switch
            ++S;
            last_switch = -S;  // Switch IDs are negative
            C[i] = last_switch;

            // Configure the switch
            X.push_back(trigger_to_next[i]);  // Exit 'X' leads to the next trigger
            Y.push_back(i == 0 ? 0 : i);     // Exit 'Y' loops back
        }
    }

    // Answer the circuit configuration
    answer(C, X, Y);
}

