void create_circuit(int M, vector<int> A) {
    int N = A.size();  // Length of sequence A.

    // Step 1: Calculate S (number of switches).
    int S = 0;
    vector<int> switch_used(M + 1, 0);  // Track how many switches are used for each trigger.
    unordered_map<int, int> switch_mapping;  // Map of switches for triggers.

    vector<int> C(M + 1, 0);  // Exit connections for origin and triggers.
    vector<int> X;  // Exit 'X' connections for switches.
    vector<int> Y;  // Exit 'Y' connections for switches.

    // Step 2: Build the circuit.
    for (int i = 0; i < N; ++i) {
        int trigger = A[i];

        // Check if we need a new switch for this trigger.
        if (switch_used[trigger] == 0) {
            ++S;
            switch_mapping[trigger] = -S;  // Assign switch number (negative).
            switch_used[trigger] = 1;
            X.push_back(trigger);  // Connect 'X' to the current trigger.
            Y.push_back(0);       // Connect 'Y' back to the origin.
        } else {
            // Update 'Y' connection for the last switch used by this trigger.
            Y[-switch_mapping[trigger] - 1] = trigger;
        }
    }

    // Connect the origin to the first trigger or switch.
    C[0] = switch_mapping[A[0]];

    // Connect all triggers sequentially.
    for (int i = 1; i < N; ++i) {
        int trigger = A[i - 1];
        int next_trigger = A[i];
        C[trigger] = switch_mapping[next_trigger];
    }

    // Connect the last trigger back to the origin.
    int last_trigger = A.back();
    C[last_trigger] = 0;

    // Step 3: Call the answer function to provide the solution.
    answer(C, X, Y);
}

