void create_circuit(int M, vector<int> A) {
    int N = A.size();

    // To store the connections for output
    vector<int> C(M + 1); // Connections for origin and triggers
    vector<int> X, Y;     // Connections for switches

    // Map each trigger to a corresponding switch
    unordered_map<int, int> trigger_to_switch;
    int switch_count = 0;

    // Construct the circuit
    int prev_device = 0; // Start from the origin
    for (int i = 0; i < N; i++) {
        int trigger = A[i];

        if (trigger_to_switch.find(trigger) == trigger_to_switch.end()) {
            // Create a new switch for this trigger
            switch_count++;
            trigger_to_switch[trigger] = -switch_count; // Assign switch ID
            X.push_back(trigger); // Connect exit 'X' of the new switch to the trigger
            Y.push_back(prev_device); // Connect exit 'Y' of the new switch to the previous device
        }

        // Connect the previous device to this switch
        C[prev_device] = trigger_to_switch[trigger];
        prev_device = trigger_to_switch[trigger];
    }

    // Finally, connect the last device back to the origin
    C[prev_device] = 0;

    // Output the circuit
    answer(C, X, Y);
}

