void create_circuit(int M, vector<int> A) {
    int N = A.size(); // Length of the sequence A

    // To store the circuit connections
    vector<int> C(M + 1, 0); // Exit connections for origin and triggers
    vector<int> X;           // Exit 'X' connections for switches
    vector<int> Y;           // Exit 'Y' connections for switches

    // Map to keep track of the last used switch for each trigger
    unordered_map<int, int> lastSwitch;

    int currentSwitch = -1; // Switch IDs start from -1

    // Build the circuit
    for (int i = 0; i < N; i++) {
        int trigger = A[i];
        if (lastSwitch.count(trigger) == 0) {
            // If this trigger hasn't been connected to a switch yet
            currentSwitch--; // Use a new switch
            lastSwitch[trigger] = currentSwitch;

            // Connect this switch to the trigger and the next trigger
            X.push_back(trigger); // Exit 'X' goes to the current trigger
            Y.push_back(0);       // Exit 'Y' goes back to the origin for now

            // Set the trigger to point to this switch
            C[trigger] = currentSwitch;
        } else {
            // If this trigger has been connected before
            int switchID = lastSwitch[trigger];
            Y[-switchID - 1] = trigger; // Update the 'Y' connection for the switch
        }
    }

    // Connect the origin to the first trigger
    C[0] = lastSwitch[A[0]];

    // Output the circuit configuration
    answer(C, X, Y);
}

