std::vector<int> find_colours(int N, std::vector<int> X, std::vector<int> Y) {
    // Resulting color array
    vector<int> colors(N, -1);

    // Step 1: Perform initial experiment to get the baseline number of components
    vector<int> E(N, -1);
    int initial_components = perform_experiment(E);

    // Step 2: Determine the color groups
    for (int i = 0; i < N; ++i) {
        // Recolor vertex `i` to the Sphinx's color (N)
        E.assign(N, -1);
        E[i] = N;
        int new_components = perform_experiment(E);

        // If the number of components increases, vertex `i` is in a unique color group
        if (new_components > initial_components) {
            colors[i] = i; // Assign a unique color to this vertex
        }
    }

    // Step 3: Propagate colors through connected components
    // Construct adjacency list from edges
    vector<vector<int>> adj(N);
    for (int i = 0; i < X.size(); ++i) {
        adj[X[i]].push_back(Y[i]);
        adj[Y[i]].push_back(X[i]);
    }

    // BFS to propagate colors
    for (int i = 0; i < N; ++i) {
        if (colors[i] != -1) { // If already colored, propagate
            queue<int> q;
            q.push(i);

            while (!q.empty()) {
                int u = q.front();
                q.pop();

                for (int v : adj[u]) {
                    if (colors[v] == -1) { // If not colored
                        // Recolor vertex `v` to the same color as `u`
                        E.assign(N, -1);
                        E[v] = colors[u];
                        int new_components = perform_experiment(E);

                        // If the number of components remains the same, they share the same color
                        if (new_components == initial_components) {
                            colors[v] = colors[u];
                            q.push(v);
                        }
                    }
                }
            }
        }
    }

    // Step 4: Assign unique colors for any uncolored vertices (unlikely but safe fallback)
    for (int i = 0; i < N; ++i) {
        if (colors[i] == -1) {
            colors[i] = i; // Assign a unique color
        }
    }

    return colors;
}

