std::vector<int> find_colours(int N, std::vector<int> X, std::vector<int> Y) {
    // The result array to store the deduced colors of the vertices
    std::vector<int> G(N, -1);

    // Step 1: Perform an initial experiment with no recoloring
    std::vector<int> E(N, -1);
    int base_components = perform_experiment(E);

    // Step 2: Deduce colors for each vertex
    for (int i = 0; i < N; ++i) {
        // Recolor vertex `i` to a unique color (e.g., Sphinx's color `N`)
        std::vector<int> E_temp(N, -1);
        E_temp[i] = N;
        int new_components = perform_experiment(E_temp);

        // If the number of components decreases, this vertex has a unique color
        if (new_components < base_components) {
            G[i] = i; // Assign the vertex a unique color based on its index
        }
    }

    // Step 3: Group vertices with the same color
    // For vertices whose colors are not yet determined, we deduce them
    for (int i = 0; i < N; ++i) {
        if (G[i] != -1) continue; // Skip vertices whose colors are already determined

        // Attempt to match this vertex's color with already known colors
        for (int color = 0; color < N; ++color) {
            if (std::find(G.begin(), G.end(), color) == G.end()) continue; // Skip unused colors

            // Recolor `i` and check if it belongs to the same component as other vertices with `color`
            std::vector<int> E_temp(N, -1);
            E_temp[i] = color;
            int new_components = perform_experiment(E_temp);

