union(bool, vector<int>) find_journey(int N, int M, vector<int> U, vector<int> V) {
    // Graph representation
    vector<vector<pair<int, int>>> adj(N); // adj[node] = list of {neighbor, canoe_id}
    vector<int> degree(N, 0); // degree of each node
    vector<bool> used(M, false); // track if a canoe is used

    // Build the graph
    for (int i = 0; i < M; ++i) {
        adj[U[i]].emplace_back(V[i], i);
        adj[V[i]].emplace_back(U[i], i);
        degree[U[i]]++;
        degree[V[i]]++;
    }

    // Check if the graph satisfies Eulerian circuit conditions
    for (int i = 0; i < N; ++i) {
        if (degree[i] % 2 != 0) {
            return false; // A node with odd degree means no Eulerian circuit
        }
    }

    // Hierholzer's algorithm for finding an Eulerian circuit
    vector<int> journey;
    stack<int> st;
    st.push(0); // Start from node 0

    while (!st.empty()) {
        int u = st.top();
        bool found = false;

        // Traverse edges from node u
        while (!adj[u].empty()) {
            auto [v, canoe_id] = adj[u].back();
            adj[u].pop_back(); // Remove the edge from adjacency list

            if (!used[canoe_id]) {
                used[canoe_id] = true; // Mark the canoe as used
                st.push(v); // Continue from the next node
                found = true;
                break;
            }
        }

        if (!found) {
            // Backtrack and add the node to the journey
            st.pop();
            if (!st.empty()) {
                journey.push_back(canoe_id);
            }
        }
    }

    // Check if we have a valid journey
    if (journey.size() > 2'000'000 || journey.empty()) {
        return false;
    }
    return journey;
}

