    for (; start_node < N; ++start_node) {
        if (!adj[start_node].empty()) break; // Find a node with at least one edge
    }
    if (start_node == N) return false; // No edges in the graph

    dfs_stack.push(start_node);
    while (!dfs_stack.empty()) {
