            if (visited[edge_index]) continue; // Skip if the edge is already used

            // Use the edge
            visited[edge_index] = true;
            stack.push(neighbor); // Move to the neighbor
            result.push_back(edge_index); // Record the edge used
            found_unvisited_edge = true;
            break;
        }

        // If no unused edge is found, backtrack
        if (!found_unvisited_edge) {
            stack.pop();
        }
    }

    // Verify if all edges have been used exactly twice
    if (result.size() != M * 2) {
        return false;
    }

    return result;
}

