void fenwick_update(int idx, long long delta, int n) {
    while (idx < n) {
        fenwick[idx] += delta;
        idx += idx & -idx;
    }
}

long long fenwick_query(int idx) {
    long long sum = 0;
    while (idx > 0) {
        sum += fenwick[idx];
        idx -= idx & -idx;
    }
    return sum;
}

long long fenwick_range_query(int l, int r) {
    return fenwick_query(r) - fenwick_query(l - 1);
}

void init(vector<int> P, vector<int> W) {
