    int n = weights.size();
    long long total_cost = 0;

    for (int i = 0; i < n; ++i) {
        int node = euler[i];
        int subtree_sum = fenwick_range_query(start[node] + 1, end[node] + 1);
        int required = max(L - subtree_sum, 0);
        int excess = max(subtree_sum - R, 0);

        if (required > 0) {
            fenwick_update(start[node] + 1, required, n + 1);
            fenwick_update(end[node] + 2, -required, n + 1);
            total_cost += 1LL * required * weights[node];
        } else if (excess > 0) {
            fenwick_update(start[node] + 1, -excess, n + 1);
            fenwick_update(end[node] + 2, excess, n + 1);
            total_cost += 1LL * excess * weights[node];
        }
    }

    return total_cost;
}

