// Forward declarations of required functions
void send(int a);
void output(int b);

// Constants
const int MAX_R = 255;

// Function to encode the message
void encode(int N, vector<int> M) {
    // Step 1: Count the frequency of each number in the message
    unordered_map<int, int> frequency;
    for (int i = 0; i < N; i++) {
        frequency[M[i]]++;
    }

    // Step 2: Send each unique value and its frequency
    for (const auto& pair : frequency) {
        int value = pair.first;
        int freq = pair.second;

        // Send the value and its frequency
        send(value);
        send(freq);
    }
}

// Function to decode the message
void decode(int N, int L, vector<int> X) {
    // Step 1: Reconstruct the frequency map
    unordered_map<int, int> frequency;

    for (int i = 0; i < L; i += 2) {
        int value = X[i];
        int freq = X[i + 1];
        frequency[value] = freq;
    }

    // Step 2: Reconstruct the original message
    vector<int> message;
    for (const auto& pair : frequency) {
        int value = pair.first;
        int freq = pair.second;

        for (int j = 0; j < freq; j++) {
            message.push_back(value);
        }
    }

    // Step 3: Output the original message in order
    for (int i = 0; i < N; i++) {
        output(message[i]);
    }
}

