                append_move(1, 0);           // Copy register 0 to register 1
                append_right(1, 1, j * k);   // Shift right by j*k to isolate the j-th integer

                // Isolate the (j+1)-th integer in register 2
                append_move(2, 0);           // Copy register 0 to register 2
