vector<int> beechtree(int N, int M, vector<int> P, vector<int> C) {
    // Adjacency list representation of the tree
    vector<vector<int>> tree(N);
    for (int i = 1; i < N; ++i) {
        tree[P[i]].push_back(i);
    }

    // Result array to store whether each subtree is beautiful
    vector<int> result(N, 1);

    // DFS function to process each subtree
    function<void(int)> dfs = [&](int node) {
        unordered_map<int, int> color_count; // Map to track color frequencies
        vector<int> order; // Order of nodes in the subtree
        order.push_back(node); // Start with the root of this subtree

        // Stack for iterative DFS
        vector<int> stack = {node};
        vector<int> parent(N, -1); // To track parent of each node in the order
        while (!stack.empty()) {
            int curr = stack.back();
            stack.pop_back();

            for (int child : tree[curr]) {
                stack.push_back(child);
                parent[child] = curr; // Track parent
                order.push_back(child);
            }
        }

        // Validate the "beautiful" property
        unordered_map<int, int> color_last_seen; // Map to track last seen position of each color
        for (int i = 1; i < order.size(); ++i) {
            int node_i = order[i];
            int color = C[node_i];

            // Find f(i): the number of times this color appears in the sequence so far
            int f_i = color_count[color]++;

            // The parent of node_i in the beautiful permutation should be order[f_i]
            if (parent[node_i] != order[f_i]) {
                result[node] = 0; // Mark this subtree as not beautiful
                return;
            }
        }
    };

    // Start DFS from the root node (0)
    for (int i = 0; i < N; ++i) {
        dfs(i);
    }

    return result;
}

