using namespace std;

vector<int> beechtree(int N, int M, vector<int> P, vector<int> C) {
    // Step 1: Build adjacency list representation of the tree
    vector<vector<int>> tree(N);
    for (int i = 1; i < N; ++i) {
        tree[P[i]].push_back(i);
    }

    // Step 2: Result array to store whether each subtree is beautiful
    vector<int> result(N, 1); // Initially assume all subtrees are beautiful

    // Helper function to check if a subtree is beautiful
    auto is_beautiful = [&](int root) -> bool {
        // Stack for DFS
        vector<int> stack = {root};
        vector<int> order; // Collect all nodes in the subtree
        unordered_map<int, int> color_count; // Track color frequencies
        unordered_map<int, int> node_to_parent; // Track parent relationships

        node_to_parent[root] = -1; // Root has no parent
        while (!stack.empty()) {
            int node = stack.back();
            stack.pop_back();
            order.push_back(node);

            // Add children to the stack
            for (int child : tree[node]) {
                stack.push_back(child);
                node_to_parent[child] = node; // Record parent
            }
        }

        // Reverse the order for bottom-up processing
        reverse(order.begin(), order.end());

        // Bottom-up validation of the subtree
        unordered_map<int, int> color_last_seen; // Maps color to last position it was seen
        for (int node : order) {
            int parent = node_to_parent[node];
            int color = C[node];

            // Determine f(i) (frequency of this color in the sequence seen so far)
            int f_i = (color_last_seen.find(color) != color_last_seen.end()) ? color_last_seen[color] : -1;

            // Check if the parent of the current node is valid
            if (parent != -1 && parent != order[f_i]) {
                return false; // Invalid parent-child relationship
            }

            // Update color_last_seen for this color
            color_last_seen[color] = node;
        }

        return true;
    };

    // Step 3: Check each subtree
    for (int r = 0; r < N; ++r) {
        if (!is_beautiful(r)) {
            result[r] = 0;
        }
    }

    return result;
}

