vector<int> beechtree(int N, int M, vector<int> P, vector<int> C) {
    // Precompute the children of each node
    vector<vector<int>> children(N);
    for (int i = 1; i < N; i++) {
        children[P[i]].push_back(i);
    }

    // Result array
    vector<int> result(N, 0);

    // Helper function to check if a subtree is beautiful
    auto isBeautiful = [&](int root) -> bool {
        // Subtree nodes and color frequencies
        vector<int> nodes;
        unordered_map<int, int> colorCount;

        // DFS to collect all nodes in the subtree
        function<void(int)> dfs = [&](int node) {
            nodes.push_back(node);
            for (int child : children[node]) {
                dfs(child);
            }
        };
        dfs(root);

        // Mapping from colors to the first occurrence index in the sequence
        unordered_map<int, int> colorToIndex;

        // Simulate the beautiful permutation construction
        for (int i = 0; i < nodes.size(); i++) {
            int node = nodes[i];
            int color = C[node];

            // Determine the parent based on the color count
            int parentIndex = (colorCount[color] > 0) ? colorToIndex[color] : 0;

            // Check if the parent aligns with the tree structure
            if (i > 0 && P[node] != nodes[parentIndex]) {
                return false;
            }

            // Update color count and mapping
            colorToIndex[color] = i;
            colorCount[color]++;
        }

        return true;
    };

    // Check each subtree
    for (int r = 0; r < N; r++) {
        if (isBeautiful(r)) {
            result[r] = 1;
        }
    }

    return result;
}

