        vector<int> stack;
        unordered_map<int, int> colorCount;
        vector<int> parent(N, -1); // Parent tracking
        stack.push_back(root);

        while (!stack.empty()) {
            int node = stack.back();
            stack.pop_back();

            // Process the current node
            if (node != root) {
                int freq = colorCount[C[node]]++;
                if (parent[node] != -1 && parent[node] != freq) {
                    return false;
                }
            }

            // Push children to stack
            for (int child : tree[node]) {
                parent[child] = colorCount[C[child]];
                stack.push_back(child);
            }
        }

        return true;
    };

    // Check each subtree
    for (int r = 0; r < N; ++r) {
        if (!isBeautiful(r)) {
            result[r] = 0;
        }
    }

    return result;
}

