#include <algorithm>
#include <numeric>

using namespace std;

const int MOD = 1000000009;

// Helper function to check if a sequence is circularly sorted
bool isCircularSorted(const vector<int>& seq, int n) {
    int offset = -1;
    for (int i = 0; i < n; ++i) {
        if (seq[i] == 1) {
            offset = i;
            break;
        }
    }
    if (offset == -1) return false;

    for (int i = 0; i < n; ++i) {
        if (seq[(offset + i) % n] != (i + 1)) {
            return false;
        }
    }
    return true;
}

// Function to check if the input sequence is a gondola sequence
int valid(int n, int inputSeq[]) {
    vector<int> seq(inputSeq, inputSeq + n);
    unordered_set<int> seen;
    int maxGondola = *max_element(seq.begin(), seq.end());

    // Check for duplicates and invalid gondola numbers
    for (int x : seq) {
        if (seen.count(x) || x < 1 || x > n + (maxGondola - n)) {
            return 0;
        }
        seen.insert(x);
    }

    // Check if the sequence is circularly sorted for the first n gondolas
    vector<int> firstNGondolas;
    for (int x : seq) {
        if (x <= n) {
            firstNGondolas.push_back(x);
        }
    }
    sort(firstNGondolas.begin(), firstNGondolas.end());
    if (!isCircularSorted(firstNGondolas, firstNGondolas.size())) {
        return 0;
    }

    return 1;
}

// Function to construct a replacement sequence
int replacement(int n, int gondolaSeq[], int replacementSeq[]) {
    vector<int> seq(gondolaSeq, gondolaSeq + n);
    unordered_set<int> existingGondolas(seq.begin(), seq.end());
    vector<int> missingGondolas;

    for (int i = 1; i <= n; ++i) {
        if (!existingGondolas.count(i)) {
            missingGondolas.push_back(i);
        }
    }

    int replacementIndex = 0;
    for (int i = 0; i < n; ++i) {
        if (seq[i] > n) {
            replacementSeq[replacementIndex++] = missingGondolas.back();
            missingGondolas.pop_back();
        }
    }

    return replacementIndex;
}

// Function to count the number of possible replacement sequences
int countReplacement(int n, int inputSeq[]) {
    if (!valid(n, inputSeq)) return 0;

    vector<int> seq(inputSeq, inputSeq + n);
    unordered_set<int> seen;
    int maxGondola = *max_element(seq.begin(), seq.end());

    // Count the number of gondolas greater than n
    int extraGondolas = 0;
    for (int x : seq) {
        if (x > n) {
            ++extraGondolas;
        }
        seen.insert(x);
    }

    // Count the number of missing gondolas
    int missingGondolas = 0;
    for (int i = 1; i <= n; ++i) {
        if (!seen.count(i)) {
            ++missingGondolas;
        }
    }

    // Compute the factorial of missingGondolas modulo MOD
    long long factorial = 1;
    for (int i = 1; i <= missingGondolas; ++i) {
        factorial = (factorial * i) % MOD;
    }

    return factorial;
}

