using namespace std;
const int MOD = 1000000009;

// Function to check if the sequence is valid
int valid(int n, int inputSeq[]) {
    unordered_set<int> seen;
    for (int i = 0; i < n; ++i) {
        if (inputSeq[i] < 1 || seen.count(inputSeq[i])) {
            return 0; // Invalid if gondola number is less than 1 or repeated
        }
        seen.insert(inputSeq[i]);
    }

    int first = inputSeq[0];
    for (int i = 1; i < n; ++i) {
        if (inputSeq[i] == (first + i - 1) % n + 1 || inputSeq[i] > n) {
            continue;
        }
        return 0; // Invalid if sequence is not circular or out of range
    }

    return 1;
}

// Function to construct a replacement sequence
int replacement(int n, int gondolaSeq[], int replacementSeq[]) {
    set<int> available; // Set to store available gondolas
    for (int i = n + 1; i <= 250000; ++i) {
        available.insert(i);
    }

    unordered_set<int> existing;
    for (int i = 0; i < n; ++i) {
        existing.insert(gondolaSeq[i]);
    }

    int l = 0; // Length of the replacement sequence
    for (int i = 0; i < n; ++i) {
        if (gondolaSeq[i] > n) {
            replacementSeq[l++] = gondolaSeq[i];
        } else if (existing.count(gondolaSeq[i]) == 0) {
            int nextReplacement = *available.begin();
            available.erase(nextReplacement);
            replacementSeq[l++] = nextReplacement;
        }
    }

    return l;
}

// Function to count the number of replacement sequences
int countReplacement(int n, int inputSeq[]) {
    if (valid(n, inputSeq) == 0) {
        return 0; // Return 0 if sequence is not valid
    }

    unordered_set<int> original;
    for (int i = 1; i <= n; ++i) {
        original.insert(i);
    }

    unordered_set<int> seen;
    for (int i = 0; i < n; ++i) {
        seen.insert(inputSeq[i]);
    }

    int missing = 0;
    for (int i = 1; i <= n; ++i) {
        if (!seen.count(i)) {
            ++missing;
        }
    }

    long long result = 1;
    for (int i = 1; i <= missing; ++i) {
        result = (result * i) % MOD; // Factorial for missing gondolas
    }

    return result;
}

