const int MOD = 1000000009;

// Function to check if the input sequence is a valid gondola sequence
int valid(int n, int inputSeq[]) {
    unordered_set<int> seen;
    int minGondola = MOD, maxGondola = 0;

    // Check for duplicates and range validity
    for (int i = 0; i < n; ++i) {
        if (seen.count(inputSeq[i])) return 0; // Duplicate found
        seen.insert(inputSeq[i]);
        minGondola = min(minGondola, inputSeq[i]);
        maxGondola = max(maxGondola, inputSeq[i]);
    }

    // Check if the sequence is circularly ordered
    for (int i = 0; i < n; ++i) {
        if (inputSeq[i] <= n) {
            int start = i;
            for (int j = 0; j < n; ++j) {
                int expected = (j + 1);
                if (inputSeq[(start + j) % n] <= n && inputSeq[(start + j) % n] != expected) {
                    return 0;
                }
            }
            break;
        }
    }

    return 1;
}

// Function to construct a replacement sequence for a given gondola sequence
int replacement(int n, int gondolaSeq[], int replacementSeq[]) {
    unordered_set<int> present;
    vector<int> missing;

    // Mark gondolas that are already present
    for (int i = 0; i < n; ++i) {
        if (gondolaSeq[i] <= n) {
            present.insert(gondolaSeq[i]);
        }
    }

    // Find missing gondolas
    for (int i = 1; i <= n; ++i) {
        if (!present.count(i)) {
            missing.push_back(i);
        }
    }

    // Fill replacement sequence
    int replacementIndex = 0;
    for (int i = 0; i < n; ++i) {
        if (gondolaSeq[i] > n) {
            replacementSeq[replacementIndex++] = missing.back();
            missing.pop_back();
        }
    }

    return replacementIndex;
}

// Function to count the number of possible replacement sequences
int countReplacement(int n, int inputSeq[]) {
    if (!valid(n, inputSeq)) return 0;

    unordered_set<int> seen;
    int replacementsNeeded = 0;
    int unusedGondolas = 0;

    // Count replacements and track unused gondolas
    for (int i = 0; i < n; ++i) {
        if (inputSeq[i] <= n) {
            seen.insert(inputSeq[i]);
        } else {
            replacementsNeeded++;
        }
    }

    // Count unused gondolas
    for (int i = 1; i <= n; ++i) {
        if (!seen.count(i)) {
            unusedGondolas++;
        }
    }

    // Calculate the number of permutations of the replacement sequence
    long long result = 1;
    for (int i = 1; i <= replacementsNeeded; ++i) {
        result = (result * unusedGondolas) % MOD;
        unusedGondolas--;
    }

    // Account for circular permutations of the starting point
    result = (result * n) % MOD;

    return result;
}

