const int MODULO = 1000000009;

// Function 1: Check if the input sequence is a valid gondola sequence
int valid(int n, std::vector<int> inputSeq) {
    std::unordered_set<int> seen;
    int minValue = *std::min_element(inputSeq.begin(), inputSeq.end());
    int maxValue = *std::max_element(inputSeq.begin(), inputSeq.end());

    // Ensure all gondola numbers are unique
    for (int num : inputSeq) {
        if (seen.count(num)) return 0;
        seen.insert(num);
    }

    // Check for valid values and order
    if (maxValue - minValue + 1 > n) return 0;

    // Check if the sequence can be circular
    int startIndex = std::find(inputSeq.begin(), inputSeq.end(), minValue) - inputSeq.begin();
    for (int i = 0; i < n; ++i) {
        int expected = (minValue + i - 1) % n + 1;
        int actual = inputSeq[(startIndex + i) % n];
        if (expected != actual && actual <= n) return 0;
    }

    return 1;
}

// Function 2: Generate a replacement sequence for a given gondola sequence
int replacement(int n, std::vector<int> gondolaSeq, std::vector<int>& replacementSeq) {
    std::unordered_set<int> seen;
    int nextGondola = n + 1;

    // Mark all gondolas initially present in the sequence
    for (int num : gondolaSeq) {
        seen.insert(num);
    }

    // Generate replacement sequence for missing gondolas
    for (int num : gondolaSeq) {
        if (num > n) {
            replacementSeq.push_back(nextGondola++);
        }
    }

    return replacementSeq.size();
}

// Function 3: Count the number of possible replacement sequences
int countReplacement(int n, std::vector<int> inputSeq) {
    if (!valid(n, inputSeq)) return 0;

    std::unordered_set<int> seen;
    int fixed = 0; // Count of gondolas <= n
    int freeSlots = 0; // Count of gondolas > n
    int nextGondola = n + 1;

    // Count gondolas already fixed and identify free slots
    for (int num : inputSeq) {
        if (num <= n) {
            seen.insert(num);
            ++fixed;
        } else {
            ++freeSlots;
        }
    }

    // Count unfixed gondolas <= n
    int unfixed = n - fixed;

    // Calculate the number of replacement sequences
    long long result = 1;
    for (int i = 0; i < freeSlots; ++i) {
        result = (result * (unfixed + i)) % MODULO;
    }

    // Multiply by the number of possible permutations of the replacements
    for (int i = 1; i <= freeSlots; ++i) {
        result = (result * i) % MODULO;
    }

    return result;
}

