    vector<vector<int64_t>> arrival_times(N + 1, vector<int64_t>(M, 0));

    // Set initial times at the airport (station 0)
    for (int i = 0; i <= N; ++i) {
        arrival_times[i][0] = buses[i].departure_time;
    }

    // Compute times for subsequent sorting stations
    for (int j = 1; j < M; ++j) {
        for (int i = 0; i <= N; ++i) {
            // Calculate the expected arrival time at station j
            int64_t expected_time = arrival_times[i][j - 1] + buses[i].travel_time_per_km * (S[j] - S[j - 1]);

            // Find the maximum expected time of buses arriving earlier at the previous station
            int64_t max_time = expected_time;
            for (int k = 0; k <= N; ++k) {
                if (arrival_times[k][j - 1] < arrival_times[i][j - 1]) {
                    max_time = max(max_time, arrival_times[k][j]);
                }
            }

            // Update the actual arrival time
            arrival_times[i][j] = max_time;
        }
    }

    // Return the arrival time of the reserve bus at the hotel (last station)
    return arrival_times[N][M - 1];
